					y = (float) paintVolume[getIndex(i, j, DigitalGraffiti::YELLOW_BALL)] / maxVolume;
					b = (float) paintVolume[getIndex(i, j, DigitalGraffiti::BLUE_BALL)] / maxVolume;
					setRybToRgb(i, j, r, y, b);
