/**
* Wall texture update
*/ 
void Wall::updateTextureWall(GLuint texture) 
{    
	if(mode != DigitalGraffiti::MODE_PAINT) 
	{
		return;
	}
	if (!wallImage) 
	{
		return;
	}
	glBindTexture(GL_TEXTURE_2D, texture);
	// Allocates memory for a texture
	try 
	{
		glTexImage2D(
			GL_TEXTURE_2D, 0,           // target, level
			GL_RGB8,                    // internal format
			wallWidth, wallHeight, 0, // width, height, border
			GL_RGB, GL_UNSIGNED_BYTE,   // external format, type
			wallImage                   // pixels
			);
	}
	catch (...) 
	{
		delete[] paintVolume;
		delete[] wallImage;
		throw;
	}
}

void Wall::saveImage(std::string imageCode)
{
	// Save in .ppm format
	std::string fileName = "saved-images\\" + imageCode + ".ppm";
	if(DigitalGraffiti::DEBUG)
	{
		printf("Saving %s\n", fileName.c_str());
	}
	FILE *f = fopen(fileName.c_str(), "wb");
	fprintf(f, "P6\n%i %i 255\n", wallWidth, wallHeight);
	for (int y=0; y<wallHeight; y++)
		for (int x=0; x<wallWidth; x++)
		{
			fputc(wallImage[getIndex(x, y, RED_INDEX)], f);   // 0 .. 255
			fputc(wallImage[getIndex(x, y, GREEN_INDEX)], f); // 0 .. 255
			fputc(wallImage[getIndex(x, y, BLUE_INDEX)], f);  // 0 .. 255
		}
		fclose(f);
}

/**
* Updates the shape, letter, and number that will be used to name the image file
* shape: the current day of the week
* letter: the current hour mapped to an alphabet letter (1->A, 2->B, ...)
* number: the current minute
*/
void Wall::updateImageCode()
{
	time_t t = time(0);
	struct tm *now = localtime(&t);

	codeShape = SHAPE_LOOKUP[now->tm_wday];
	// ASCII conversion from [0, 23] -> [a, x]
	codeLetter = (char)(now->tm_hour + 97);
	stringstream ss;
	ss << now->tm_min;
	codeNumber = ss.str();
	imageCode = codeShape + "-" + codeLetter + "-" + codeNumber;

	if(DigitalGraffiti::DEBUG)
	{
		printf("tm_wday=%u, tm_hour=%u, tm_min=%u, code=%s\n", now->tm_wday, now->tm_hour, now->tm_min, imageCode.c_str());
	}
}

std::string Wall::getImageCode()
{
	return imageCode;
}